import React from 'react';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import { Redirect } from 'react-router-dom';
import {v4 as uuid} from 'uuid'


class CreateSessionForm extends React.Component {

    state = {
        redirect: false,
        name: '',
        id: null
    }

    handleSumbit = () => {

        this.state.id = uuid.v4();
        this.props.createSession({name: this.state.name, id: this.state.id });
        this.setState(() => ({
            name: '',
            redirect: true
        }))
    }

    onChange = (e) => {
        this.setState({ [e.target.name]: e.target.value })
    }

    render() {
        if (this.state.redirect === true) {
            return <Redirect to={`/${this.state.id}`}/>
        }

    return (
        <div>
    <Form>
        <Form.Group controlId="formBasicEm">
          <Form.Label>Session Name: </Form.Label>
          <Form.Control onChange={this.onChange} value={this.state.name}  name="name" type="text" placeholder="Ener name" />
        </Form.Group>

        <Form.Group controlId="exampleForm.ControlSelect1">
            <Form.Label>Choose cards:</Form.Label>
            <Form.Control as="select">
                <option>1,3,8,20,50,100</option>
                <option>1,2,3,4,5</option>
                <option>XS,S,M,L,XL,XXL</option>
            </Form.Control>
        </Form.Group>
        <Button onClick={this.handleSumbit} variant="primary" type="submit">
          Submit
         </Button>
    </Form>
    </div>
    )
    }
}

export default CreateSessionForm;
