import React, {useEffect, useState} from 'react';
import { Link } from 'react-router-dom';
import Breadcrumb from 'react-bootstrap/Breadcrumb';
import './Nav.css';

const Nav = () => {

  const [name, setName] = useState('');

  useEffect(() => {
    let userName = localStorage.getItem('userName');
    if(userName !== null) {
      setName(userName);
    }
  });

  return (
    <nav>
      <div className="logo">
        <Link to='/'>
          <h4>Logo</h4> 
        </Link>
      </div>
      <div className="name">
      <h4 id='user'>{name}</h4>
      </div>
    </nav>
  )
}

export default Nav;
