import React, { useEffect, useState, useDebugValue } from 'react';
import queryString from 'query-string';
import io from 'socket.io-client'
import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';


let socket;

const SessionDetail = ({ match, location }) => {

    const [name, setName] = useState('');
    const [sessionId, setSessionId] = useState('');
    const [modalShow, setModalShow] = React.useState(true);

    const ENDPOINT = 'localhost:5000';



    useEffect(() => {
        const id = match.params.id;

        setSessionId(id);

        // socket = io(ENDPOINT);

        // setName(name);
        // setSessionId(id);

        // console.log(sessionId);

        // socket.emit('join', {name, id});

        // return () => {
        //     socket.emit('disconnect');
        //     socket.off();
        // }

    }, [ENDPOINT, location.search]);

    const handleChange = (e) => {
        setName(e.target.value);
    }

    const hide = () => {
        setModalShow(false);
        console.log("hide");
    }

    function MyVerticallyCenteredModal(props) {

        return (
          <Modal
            show={props.show}
            onHide={props.onHide}
            size="sm"
            aria-labelledby="contained-modal-title-vcenter"
            centered
          >
            <Modal.Header>
              <Modal.Title id="contained-modal-title-vcenter">
                Enter display name
              </Modal.Title>
            </Modal.Header>
            <Modal.Body>
            {/* <Form.Group controlId="formBasicEm">
             <Form.Control onChange={props.handlechange} value={name} name="name" type="text" placeholder="Ener name" />
            </Form.Group> */}
            <Button onClick={props.onHide} block >Close</Button>

            </Modal.Body>
          </Modal>
        );
      }

    return (
        <div>
            <Form.Group controlId="formBasicEm">
             <Form.Control onChange={handleChange} value={name} name="name" type="text" placeholder="Ener name" />
            </Form.Group>

            <h1>id: {sessionId}</h1>
            <h1>name: {name} </h1>


        <MyVerticallyCenteredModal show={modalShow} handlechange={handleChange} onHide={hide} />

        </div>

    );
}

export default SessionDetail;
