            // socket = io(ENDPOINT);

            // socket.emit('join', {name, id: sessionId});

            // return () => {
            //     socket.emit('disconnect');
            //     socket.off();
            // }
            }

    },[name]);

    const saveName = (data) => {
        setName(data);
        document.getElementById('user').innerText = data;
        localStorage.setItem('userName', data);
    }

    return (
        <div>
            <h1>id: {sessionId}</h1>
            <h1>name: {name} </h1>

            <Dialog enterName={saveName} />

        </div>

    );
}

export default SessionDetail;
